{% style %}
.step-guide__spaced-row {
  padding-top: 40px;
  padding-bottom: 40px;
}

.small-container {
  max-width: 1200px;
  padding-left: 40px;
  padding-right: 40px;
  margin-left: auto;
  margin-right: auto;
}

.step_selector p {
  text-align: center;
  font-size: 14px;
}

.step_selector span {
  font-size: 2.5em;
}

.step-label {
  font-size: .7em;
  text-transform: uppercase;
}

.step-heading_container {
  margin: 1em auto 2em;
  max-width: 800px;
}

.step-selectors_container {
  width: 100%;
  display: flex;
  border: 1px solid #e2e8e0;
  border-collapse: collapse;
}

.step_selector {
  flex: 1;
  border-left: 1px solid #e2e8e0;
  transition: all 500ms ease;
  cursor: pointer;
  padding: 10px;
  position: relative;
  background: rgba(236, 240, 235, 0);
}

.step_selector::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

.step_selector:first-child {
  border-left: none;
}

.active-step {
  background: rgba(236, 240, 235, 1);
}

.step_selector p {
  width: fit-content;
  margin: 0 auto;
}

.step-content_container {
  display: flex;
  width: 100%;
  border: 1px solid #e2e8e0;
  border-top: none;
  border-collapse: collapse;
  overflow: hidden;
}

.step_slider {
  width: 100%;
  display: flex;
  transition: transform 500ms ease;
}

.step_content {
  flex: 1 0 100%;
  padding: 10px 10px 15px;
  background: #fafafa;
}

.step-img {
  display: flex;
  justify-content: center;
}

.step-img img {
  min-width: 200px;
  min-height: 200px;
  width: 200px;
  height: 200px;
  border-radius: 50%;
  box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
}

@media (max-width: 1300px) {
  .small-container {
    padding-left: 30px;
    padding-right: 30px;
  }
}

@media (max-width: 767px) {
  .small-container {
    padding-left: 16px;
    padding-right: 16px;
  }
}

@media (min-width: 768px) {
  .main-step_container {
      display: flex;
  }

  .step-selectors_container {
      flex-direction: column;
      flex: 0 0 15%;
  }

  .step-content_container {
      border-top: 1px solid #e2e8e0;
      border-left: 0;
      flex: 0 0 85%;
  }

  .step_selector {
      border-left: none;
      border-top: 1px solid #e2e8e0;
      display: flex;
      align-items: center;
  }

  .step_selector:first-child {
      border-top: none;
  }

  .step-info_container {
      display: flex;
      gap: 20px;
  }

  .step_content {
    padding: 20px 40px;
  }

  .step-img {
    align-self: center;
  }
}
{% endstyle %}
