{% style %}
.ingredients-table, 
.shipping-table, 
.kit-table {
  height: auto;
  width: 100%;
  margin-left: auto;
  margin-right: auto;
  border: 1px solid #E2E2E2;
  border-collapse: collapse;
  border-spacing: 0;
  box-sizing: border-box;
}

.ship-tb-head__cost, 
.ship-tb-data__cost {
  width: 20%;
}

.ing-tb-head__active, 
.ing-tb-data__active,
.ing-tb-head__product, 
.ing-tb-data__product, 
.ship-tb-head__threshold, 
.ship-tb-data__threshold, 
.kit-tb-head__size, 
.kit-tb-data__size {
  width: 30%;
}

.ing-tb-head__inert, 
.ing-tb-data__inert {
  width: 40%;
}

.ship-tb-head__options, 
.ship-tb-data__options {
  width: 50%;
}

.kit-tb-head__kit-includes, 
.kit-tb-data__kit-includes {
  width: 70%;
}

.ing-tb-head,
.ship-tb-head, 
.kit-tb-head,  
.ing-tb-data, 
.ship-tb-data, 
.kit-tb-data {
  vertical-align: middle;
  text-align: center;
  border: 1px solid #E2E2E2;
}

.ing-tb-head, 
.ship-tb-head,
.kit-tb-head {
  text-decoration: underline;
  text-transform: uppercase;
  font-weight: 700;
  font-size: 14px;
  padding: 0.4em;
}

.ing-tb-data, 
.ship-tb-data, 
.kit-tb-data {
  border: 1px solid #E2E2E2;
  font-size: 12px;
}

.ship-tb-data__time {
  font-size: 10px;
}

.ing-tb__learn-more, 
.ship-tb__learn-more {
  text-align: center;
  font-size: 0.8em;
  margin-bottom: 0;
}
{% endstyle %}
